    private String getValues(String filter) {
        String resultList = "";
        List result = new ArrayList();
        String ex = "\"Aaron\",\"Abby\",\"Abdelkader\"";
        List resourceList = ResourceService.getInstance().getResourceList();
        for (int i = 0; i < resourceList.size(); i++) {
            Resource r = (Resource) resourceList.get(i);
            switch (filter) {
                case "firstName":
                    if(null!=r.getFirstName()&&!"".equalsIgnoreCase(r.getFirstName())&&!result.contains(r.getFirstName().replaceAll("\"", "").trim())){
                        resultList += "\"" + r.getFirstName().replaceAll("\"", "").trim() + "\",";
                        result.add(r.getFirstName().replaceAll("\"", "").trim());
                    }
//                    return ex;
                    break;
                case "lastName":
                    if(null!=r.getLastName()&&!"".equalsIgnoreCase(r.getLastName())&&!result.contains(r.getLastName().trim())){
                        resultList += "\"" + r.getLastName().trim() + "\",";
                        result.add(r.getLastName().trim());
                    }
                    break;
                case "companyName":
                    if(null!=r.getCompanyName()&&!"".equalsIgnoreCase(r.getCompanyName())&&!result.contains(r.getCompanyName().trim())){
                        resultList += "\"" + r.getCompanyName().trim() + "\",";
                        result.add(r.getCompanyName().trim());
                    }
                    break;
                case "resourceName":  
                     if(null!=r.getFirstName()&&!"".equalsIgnoreCase(r.getFirstName())&&!result.contains(r.getFirstName().replaceAll("\"", "").trim())){
                        resultList += "\"" + r.getFirstName().replaceAll("\"", "").trim();
                        resultList += " " + r.getLastName().replaceAll("\"", "").trim() + "\",";
                        result.add(r.getFirstName().replaceAll("\"", "").trim());
                    }
                     if(null!=r.getCompanyName()&&!"".equalsIgnoreCase(r.getCompanyName())&&!result.contains(r.getCompanyName().trim())){
                        resultList += "\"" + r.getCompanyName().trim() + "\",";
                        result.add(r.getCompanyName().trim());
                    }
//                    return ex;
                    break;
            }
        }
        return resultList.substring(0, resultList.length() - 1);
    }
